mapper.update:
update tb_product
product_name=#{productName}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, normal_price=#{promotionPrice}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, product_category_id=#{productCategory.productCategoryId}
where product_id = #{productId} and shop_id = #{shop.shopId}

mapper.select:
select product_id, product_name, product_desc, img_addr, normal_price, promotion_price, priority, create_time, last_edit_time, enable_status, product_category_id, shop_id from tb_product
and shop_id = #{productCondition.shop.shopId} and product_category_id = #{productCondition.productCategory.productCategoryId} and product_name like '%${productCondition.productName}%' and enable_status = #{productCondition.enableStatus}
order by priority desc limit #{rowIndex}, #{pageSize};

--         count(1)表示：如果where选择的列的组合中，有不为null的行，则计数++
        select count(1) from tb_product

            and shop_id = #{productCondition.shop.shopId}
            and product_category_id = #{productCondition.productCategory.productCategoryId}
            and product_name like '%${productCondition.productName}%'
            and enable_status = #{productCondition.enableStatus}
